    def tk(self):
        tk = self.tb_tknv.text()
        nv = nhanvien
        data = nv.timkiemsdt(tk)
        self.nhaptable(data)

    def them(self):
        sodt = self.tb_sdt.text()
        email = self.tb_em.text()
        vtri = self.cb_vt.currentText()
        gt = self.cb_gtnv.currentText()
        ca = self.cb_ca.currentText()
        nv = nhanvien(sodt, email, vtri, gt, ca)
        nv.them()
        self.hienthi()

    def sua(self):
        sodt = self.tb_sdt.text()
        email = self.tb_em.text()
        vtri = self.cb_vt.currentText()
        gt = self.cb_gtnv.currentText()
        ca = self.cb_ca.currentText()
        nv = nhanvien(sodt, email, vtri, gt, ca)
        nv.sua()
        self.hienthi()

    def xoa(self):
        sodt = self.tb_sdt.text()
        nv = nhanvien
        nv.xoa(sodt)
        self.hienthi()

    def hienthi(self):
        nv = nhanvien
        data = nv.hienthi()
        self.nhaptable(data)

    def nhaptable(self, data):
        self.tableWidget.setRowCount(0)
        for row_data in data:
            row_position = self.tableWidget.rowCount()
            self.tableWidget.insertRow(row_position)
            for column_index, cell_data in enumerate(row_data):
                self.tableWidget.setItem(row_position, column_index, QTableWidgetItem(str(cell_data)))

    def cellclick(self):
        row = self.tableWidget.currentRow()
        self.tb_sdt.setText(self.tableWidget.item(row, 0).text())
        self.tb_em.setText(self.tableWidget.item(row, 1).text())
        self.cb_vt.setCurrentText(self.tableWidget.item(row, 2).text())
        self.cb_gtnv.setCurrentText(self.tableWidget.item(row, 3).text())
        self.cb_ca.setCurrentText(self.tableWidget.item(row, 4).text())

