    def hienthi(self):      
        dh = donhang
        data = dh.hienthi(self.dt_t.text(),self.dt_d.text())
        sotc = len(data)
        tongtien = 0
        for i in range(sotc):
            tongtien = tongtien + int(data[i][6])
        dv = dichvu      
        datadv = dv.hienthithongke(self.dt_t.text(),self.dt_d.text())
        tamthom = 0
        cattia = 0
        phongdai = 0
        kysinh = 0
        for i in range(len(datadv)):
            if (datadv[i][4]==("true")):
                tamthom = tamthom + 1000000
            if (datadv[i][5]==("true")):
                cattia = cattia + 800000
            if (datadv[i][6]==("true")):
                phongdai = phongdai + 2000000
            if (datadv[i][7]==("true")):
                kysinh = kysinh + 1500000
        tongtiendv = tamthom + cattia + phongdai + kysinh
        tamthom = (100/tongtiendv)*tamthom
        cattia =(100/tongtiendv)*cattia
        phongdai = (100/tongtiendv)*phongdai
        kysinh = (100/tongtiendv)*kysinh
        self.lb_tthom.setText(str(round(tamthom,2))+"%")
        self.lb_ctl.setText(str(round(cattia,2))+"%")
        self.lb_pdai.setText(str(round(phongdai,2))+"%")
        self.lb_pks.setText(str(round(kysinh,2))+"%")
        self.lb_tdtdv.setText(str(tongtiendv)+"VND")
        self.lb_sotc.setText(str(sotc))
        self.lb_tdt.setText(str(tongtien)+" VND")
        self.nhaptable(data)
    
    def nhaptable(self,data):
        self.tableWidget.setRowCount(0)
        for row_data in data:
            row_position = self.tableWidget.rowCount()
            self.tableWidget.insertRow(row_position)
            for column_index, cell_data in enumerate(row_data):
                self.tableWidget.setItem(row_position, column_index, QTableWidgetItem(str(cell_data)))

